function AspNetPager(page) {
    $('#Page').val(page);
    $('#mainForm').submit()
}

function DivLayer(shift, title, content) {
    var width = arguments[3] || '600px'
    var height = arguments[4] || '320px'

    layer.open({
        type: 1,
        skin: 'layui-layer-molv',
        shift: shift,
        shade: 0.6,
        title: title,
        content: content,
        area: [width, height],
        cancel: function (index) {
            layer.close(index);
        }
    })
}

function IFreamLayer(shift, title, content) {
    var width = arguments[3] || '600px'
    var height = arguments[4] || '320px'

    layer.open({
        type: 2,
        skin: 'layui-layer-molv',
        shift: shift,
        shade: 0.6,
        title: title,
        content: content,
        area: [width, height],
        cancel: function (index) {
            layer.close(index);
        }
    })
}
